function generateTemplate(t){let e="",n="";for(let o=0;o<t.length;o++)o>0?(e+='<button class="btn-tab" onclick=\'showContent(event, "content-'+o+'", '+o+")'>"+t[o].title+"</button>",n+='<button class="btn-accordion" onclick=\'showContent(event,"content-'+o+'", '+o+")'>"+t[o].title+"</button>",n+='<div class="content" id="content-'+o+'" style="display: none;">'+t[o].content+"</div>"):(e+='<button class="btn-tab active" onclick=\'showContent(event,"content-'+o+'", '+o+")'>"+t[o].title+"</button>",n+='<button class="btn-accordion active" onclick=\'showContent(event,"content-'+o+'", '+o+")'>"+t[o].title+"</button>",n+='<div class="content show" id="content-'+o+'">'+t[o].content+"</div>");document.getElementById("tab_bar").innerHTML=e,document.getElementById("content_wrapper").innerHTML=n}function showContent(t,e,n){let o,c,s,a;for(c=document.getElementsByClassName("content"),o=0;o<c.length;o++)c[o].style.display="none",c[o].className=c[o].className.replace(" show","");for(s=document.getElementsByClassName("btn-tab"),o=0;o<s.length;o++)s[o].className=s[o].className.replace(" active","");for(s[n].className+=" active",a=document.getElementsByClassName("btn-accordion"),o=0;o<a.length;o++)a[o].className=a[o].className.replace(" active","");a[n].className+=" active",document.getElementById(e).style.display="block",setTimeout(()=>{c[n].className+=" show"},15)}fetch("https://raw.githubusercontent.com/mindarc/frontend-assessment/master/data.json").then((function(t){return t.json()})).then((function(t){generateTemplate(t)}));